class Friends{constructor(e){this.friendToggle=e,this.toggleFriend()}toggleFriend(){$(this.friendToggle).click((function(e){e.preventDefault();let t=this;console.log(t),$.ajax({type:"Post",url:$(t).attr("href")}).done((e=>{let o=$(t).attr("data-value");console.log(e.data.isFriend),console.log(e.data.friends),o=1==e.data.isFriend?"Add Friend":"Remove Friend",$(t).attr("data-value",o),$(t).html(`${o}`)})).fail((e=>{console.log("error in completing the request",e)}))}))}}